using System;\nusing System.Net;\nusing System.Net.Security;\nusing System.Security.Cryptography.X509Certificates;\nusing System.IO;\n\nclass TlsExample\n{\n    static void Main(string[] args)\n    {\n        // Create a TCP connection to the server\n        TcpClient client = new TcpClient("example.com", 443);\n\n        // Create an SSL stream\n        SslStream sslStream = new SslStream(client.GetStream(), false, ValidateServerCertificate, null);\n\n        // Authenticate the server\n        sslStream.AuthenticateAsClient("example.com");\n\n        // Send and receive data over the SSL stream\n        byte[] buffer = Encoding.UTF8.GetBytes("Hello, server!");\n        sslStream.Write(buffer, 0, buffer.Length);\n        sslStream.Flush();\n\n        buffer = new byte[1024];\n        int bytesRead = sslStream.Read(buffer, 0, buffer.Length);\n        Console.WriteLine(Encoding.UTF8.GetString(buffer, 0, bytesRead));\n\n        // Close the SSL stream\n        sslStream.Close();\n        client.Close();\n    }\n\n    static bool ValidateServerCertificate(object sender, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)\n    {\n        // Validate the server certificate here\n        return true;\n    }\n}