using System.DirectoryServices;\n\n// Create a DirectoryEntry object with the domain credentials\nDirectoryEntry entry = new DirectoryEntry("LDAP://yourdomain.com", \n    "yourusername", "yourpassword");\n\n// Create a DirectorySearcher object with the DirectoryEntry\nDirectorySearcher searcher = new DirectorySearcher(entry);\n\n// Define the search filter and properties to retrieve\nsearcher.Filter = "(&(objectClass=user)(samAccountName=yourtargetusername))";\nsearcher.PropertiesToLoad.Add("sn");\nsearcher.PropertiesToLoad.Add("givenName");\nsearcher.PropertiesToLoad.Add("mail");\n\n// Execute the search\nSearchResult result = searcher.FindOne();