using System;\nusing System.IO;\nusing Microsoft.Office.Interop.Word;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Open the Word document\n        Application wordApp = new Application();\n        Document document = wordApp.Documents.Open("path/to/your/document.docx");\n\n        // Populate the form fields\n        foreach (FormField field in document.FormFields)\n        {\n            switch (field.Name)\n            {\n                case "TextField1":\n                    field.Result = "John Doe";\n                    break;\n                case "TextField2":\n                    field.Result = "johndoe@example.com";\n                    break;\n                // Add more fields as needed\n            }\n        }\n\n        // Print the document\n        document.PrintOut();\n\n        // Clean up\n        document.Close();\n        wordApp.Quit();\n    }\n}