// Example code to test MSDTC configuration\nusing System.Transactions;\n\nclass MsdtcTest\n{\n    static void Main(string[] args)\n    {\n        string serverName = "your_server_name";\n\n        try\n        {\n            using (var scope = new TransactionScope())\n            {\n                // Perform a transactional operation\n                using (var conn = new SqlConnection("Data Source=" + serverName + ";Initial Catalog=your_database;Integrated Security=True"))\n                {\n                    conn.Open();\n                    // Perform a query or other operation\n                }\n                scope.Complete();\n            }\n        }\n        catch (TransactionManagerCommunicationException ex)\n        {\n            Console.WriteLine("MSDTC communication failed: " + ex.Message);\n        }\n    }\n}