using (SqlConnection sqlConn = new SqlConnection("your_connection_string"))\n{\n    sqlConn.Open();\n\n    using (OleDbConnection accessConn = new OleDbConnection("your_access_connection_string"))\n    {\n        accessConn.Open();\n\n        using (SqlCommand sqlCmd = new SqlCommand("SELECT * FROM your_sql_table", sqlConn))\n        {\n            using (OleDbCommand accessCmd = new OleDbCommand("INSERT INTO your_access_table VALUES (@param1, @param2, ...)", accessConn))\n            {\n                accessCmd.Parameters.AddWithValue("@param1", SqlDbType.Int);\n                accessCmd.Parameters.AddWithValue("@param2", SqlDbType.NVarChar);\n\n                using (SqlDataReader sqlReader = sqlCmd.ExecuteReader())\n                {\n                    while (sqlReader.Read())\n                    {\n                        accessCmd.Parameters["@param1"].Value = sqlReader["column1"];\n                        accessCmd.Parameters["@param2"].Value = sqlReader["column2"];\n\n                        accessCmd.ExecuteNonQuery();\n                    }\n                }\n            }\n        }\n    }\n}