using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        const int iterations = 10000000;\n        const double n = 10.0;\n\n        var sw = Stopwatch.StartNew();\n        for (int i = 0; i < iterations; i++)\n        {\n            double result = n * n * n;\n        }\n        sw.Stop();\n        Console.WriteLine($"Multiply operator: {sw.ElapsedMilliseconds} ms");\n\n        sw.Restart();\n        for (int i = 0; i < iterations; i++)\n        {\n            double result = Math.Pow(n, 3);\n        }\n        sw.Stop();\n        Console.WriteLine($"Math.Pow: {sw.ElapsedMilliseconds} ms");\n    }\n}