public class Calculator\n{\n    private int result;\n\n    public int Add(int a, int b)\n    {\n        result = a + b;\n        return result;\n    }\n\n    private int Multiply(int a, int b)\n    {\n        return a * b;\n    }\n\n    public static int GetPi()\n    {\n        return 3.14;\n    }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Calculator calc = new Calculator();\n        int sum = calc.Add(2, 3); // Accessing public method\n        // Can't access private method Multiply directly\n        double pi = Calculator.GetPi(); // Accessing public static method\n    }\n}