using System.Windows.Forms;\n\n// ...\n\nstring str1 = "Test";\nstring str2 = "Test123";\nstring common = "Add this text";\n\nSizeF textSize1 = TextRenderer.MeasureText(str1, Font);\nSizeF textSize2 = TextRenderer.MeasureText(str2, Font);\n\nint padWidth = 20 * (int)Font.Size;\nstr1 = str1.PadRight(padWidth - (int)textSize1.Width);\nstr2 = str2.PadRight(padWidth - (int)textSize2.Width);\n\nMessageBox.Show(str1 + common + "\n" + str2 + common);