// Form1.cs\nusing System;\nusing System.Collections.Generic;\nusing System.ComponentModel;\nusing System.Data;\nusing System.Drawing;\nusing System.Linq;\nusing System.Text;\nusing System.Windows.Forms;\nusing System.ComponentModel.Composition;\nusing System.ComponentModel.Composition.Hosting;\n\nnamespace Winforms_Mef\n{\n    public interface IForm\n    {\n        void AddTab(TabPage tabPage);\n    }\n\n    public interface ITab\n    {\n        TabPage[] GetTabPages();\n    }\n\n    public partial class Form1 : Form, IForm\n    {\n        private CompositionContainer _container;\n\n        [ImportMany]\n        public IEnumerable<ITab> Tabs { get; set; }\n\n        public Form1()\n        {\n            Compose();\n            InitializeComponent();\n        }\n\n        private void Compose()\n        {\n            var catalog = new DirectoryCatalog(".", "*.dll");\n            _container = new CompositionContainer(catalog);\n            _container.ComposeParts(this);\n        }\n\n        public void AddTab(TabPage tabPage)\n        {\n            tabControl1.Controls.Add(tabPage);\n        }\n    }\n}\n\n// Form2.cs\nusing System;\nusing System.Collections.Generic;\nusing System.ComponentModel;\nusing System.Data;\nusing System.Drawing;\nusing System.Linq;\nusing System.Text;\nusing System.Windows.Forms;\nusing System.ComponentModel.Composition;\n\nnamespace Winforms_Mef\n{\n    [Export(typeof(ITab))]\n    public partial class Form2 : Form, ITab\n    {\n        public Form2()\n        {\n            InitializeComponent();\n        }\n\n        public TabPage[] GetTabPages()\n        {\n            return tabControl1.Controls.OfType<TabPage>().ToArray();\n        }\n    }\n}