using System;\nusing System.Collections.Generic;\nusing System.Threading;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var myCollection = new List<int> { 1, 2, 3 };\n\n        // Simulate multiple threads accessing the collection\n        for (int i = 0; i < 5; i++)\n        {\n            int threadId = i;\n            ThreadPool.QueueUserWorkItem(state =>\n            {\n                Console.WriteLine($"Thread {Thread.CurrentThread.ManagedThreadId} is starting");\n                foreach (var item in myCollection)\n                {\n                    Console.WriteLine($"Thread {Thread.CurrentThread.ManagedThreadId} is iterating over myCollection");\n                }\n                Console.WriteLine($"Thread {Thread.CurrentThread.ManagedThreadId} is finished");\n            });\n        }\n\n        Console.ReadLine();\n    }\n}