public class ViewStateStore\n{\n    private static readonly Dictionary<Guid, object> _viewStates = new Dictionary<Guid, object>();\n\n    public static Guid StoreViewState(object viewState)\n    {\n        var guid = Guid.NewGuid();\n        _viewStates.Add(guid, viewState);\n        return guid;\n    }\n\n    public static object GetViewState(Guid guid)\n    {\n        return _viewStates[guid];\n    }\n\n    public static void RemoveViewState(Guid guid)\n    {\n        _viewStates.Remove(guid);\n    }\n}