using Oracle.ManagedDataAccess.Client;\nusing Oracle.ManagedDataAccess.Types;\n\n// Create a strongly-typed DataSet\npublic class MyDataSet : DataSet\n{\n    public MyDataSet() { }\n}\n\n// Create a TableAdapter that uses the strongly-typed DataSet\npublic class MyTableAdapter : OracleDataAdapter\n{\n    public MyTableAdapter(MyDataSet dataSet) : base(dataSet) { }\n}\n\n// Use the TableAdapter to execute the stored procedure\nMyDataSet dataSet = new MyDataSet();\nMyTableAdapter adapter = new MyTableAdapter(dataSet);\n\n// Create an Oracle connection and command\nOracleConnection conn = new OracleConnection("Your connection string");\nOracleCommand cmd = new OracleCommand("Your stored procedure name", conn);\ncmd.CommandType = CommandType.StoredProcedure;\n\n// Add the strongly-typed DataSet as an output parameter\ncmd.Parameters.Add(new OracleParameter("p_out_data", OracleDbType.RefCursor, ParameterDirection.Output));\n\n// Execute the stored procedure\nconn.Open();\ncmd.ExecuteNonQuery();\n\n// Fetch the data from the REF CURSOR\nOracleDataReader reader = ((OracleRefCursor)cmd.Parameters["p_out_data"].Value).GetDataReader();\ndataSet.Load(reader, LoadOption.OverwriteChanges);\n\n// Close the connection and dispose of the command\nconn.Close();\ncmd.Dispose();