using System.Drawing;\nusing System.Drawing.Imaging;\nusing System.IO;\n\npublic void ChangeImageResolution(string inputFile, string outputFile, float horizontalResolution, float verticalResolution)\n{\n    using (Image image = Image.FromFile(inputFile))\n    {\n        image.HorizontalResolution = horizontalResolution;\n        image.VerticalResolution = verticalResolution;\n\n        using (FileStream outputStream = new FileStream(outputFile, FileMode.Create))\n        {\n            image.Save(outputStream, ImageFormat.Jpeg);\n        }\n    }\n}