using System.Net.Http;\nusing System.Text;\n\n// Assume you have the API endpoint and credentials\nstring apiUrl = "https://restaurant-api.com/orders";\nstring apiKey = "your_api_key";\n\n// Create an order object\nvar order = new\n{\n    CustomerName = "John Doe",\n    OrderItems = new[]\n    {\n        new { MenuItemId = 1, Quantity = 2 },\n        new { MenuItemId = 2, Quantity = 1 }\n    }\n};\n\n// Serialize the order object to JSON\nvar json = JsonConvert.SerializeObject(order);\n\n// Create an HTTP client and send the request\nvar client = new HttpClient();\nclient.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", apiKey);\n\nvar response = await client.PostAsync(apiUrl, new StringContent(json, Encoding.UTF8, "application/json"));\n\n// Check the response status code\nif (response.IsSuccessStatusCode)\n{\n    Console.WriteLine("Order sent successfully!");\n}\nelse\n{\n    Console.WriteLine("Error sending order: " + response.StatusCode);\n}