public ActionResult GenerateLetter(LetterModel model)\n{\n    using (var ms = new MemoryStream())\n    {\n        var doc = new Document();\n        PdfWriter.GetInstance(doc, ms);\n        doc.Open();\n\n        doc.Add(new Paragraph(model.Title, new Font(Font.FontFamily.HELVETICA, 24)));\n        doc.Add(new Paragraph(model.Body));\n        doc.Add(new Paragraph("Sincerely,"));\n        doc.Add(new Paragraph(model.Signature));\n\n        doc.Close();\n        return File(ms.ToArray(), "application/pdf", "letter.pdf");\n    }\n}