// Service side (named pipe server)\nusing System.IO.Pipes;\nusing System.Text;\n\nclass MyService\n{\n    private NamedPipeServerStream pipeServer;\n\n    public MyService()\n    {\n        pipeServer = new NamedPipeServerStream("MyPipe", PipeDirection.Out);\n    }\n\n    public void SendMessage(string message)\n    {\n        pipeServer.WaitForConnection();\n        byte[] buffer = Encoding.UTF8.GetBytes(message);\n        pipeServer.Write(buffer, 0, buffer.Length);\n        pipeServer.Flush();\n    }\n}\n\n// GUI application side (named pipe client)\nusing System.IO.Pipes;\nusing System.Text;\n\nclass MyGuiApp\n{\n    private NamedPipeClientStream pipeClient;\n\n    public MyGuiApp()\n    {\n        pipeClient = new NamedPipeClientStream(".", "MyPipe", PipeDirection.In);\n    }\n\n    public void ReceiveMessage()\n    {\n        pipeClient.Connect();\n        byte[] buffer = new byte[1024];\n        pipeClient.Read(buffer, 0, buffer.Length);\n        string message = Encoding.UTF8.GetString(buffer);\n        MessageBox.Show(message);\n    }\n}