var collections = new[]\n{\n    new[] { new { ID = 1, Amount = 4 }, new { ID = 2, Amount = 7 } },\n    new[] { new { ID = 1, Amount = 2 }, new { ID = 2, Amount = 4 } },\n    new[] { new { ID = 1, Amount = 5 }, new { ID = 2, Amount = 3 } }\n};\n\nvar averages = collections\n    .SelectMany(c => c)\n    .GroupBy(x => x.ID)\n    .Select(g => new { ID = g.Key, Avg = g.Average(x => x.Amount) });\n\nforeach (var avg in averages)\n{\n    Console.WriteLine($"ID: {avg.ID}, Avg: {avg.Avg}");\n}