using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlString = "<a>\n    <b>zyx</b>\n    <b>wvu</b>\n    <b>tsr</b>\n    <b>qpo</b>\n</a>";\n        string xpath = "a/b[.='tsr']";\n\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xmlString);\n\n        XmlNode selectedNode = xmlDoc.SelectSingleNode(xpath);\n\n        string outerXml = selectedNode.OuterXml;\n        int index = xmlString.IndexOf(outerXml);\n\n        Console.WriteLine(index); // Outputs: 23\n    }\n}