using System;\nusing System.Data.SqlClient;\nusing Excel = Microsoft.Office.Interop.Excel;\n\nnamespace ExcelAddIn\n{\n    public class ExcelAddIn\n    {\n        private Excel.Application _excelApp;\n\n        public void ExportDataToDatabase()\n        {\n            // Get the active worksheet\n            Excel.Worksheet worksheet = _excelApp.ActiveSheet;\n\n            // Get the data from the specified cells\n            string data1 = worksheet.Cells[1, 1].Value.ToString();\n            string data2 = worksheet.Cells[2, 1].Value.ToString();\n\n            // Connect to the database\n            string connectionString = "Server=myServerAddress;Database=myDataBase;User Id=myUsername;Password=myPassword;";\n            using (SqlConnection connection = new SqlConnection(connectionString))\n            {\n                connection.Open();\n\n                // Insert data into the database\n                string query = "INSERT INTO myTable (column1, column2) VALUES (@data1, @data2)";\n                using (SqlCommand command = new SqlCommand(query, connection))\n                {\n                    command.Parameters.AddWithValue("@data1", data1);\n                    command.Parameters.AddWithValue("@data2", data2);\n                    command.ExecuteNonQuery();\n                }\n            }\n        }\n\n        public void ButtonClick()\n        {\n            ExportDataToDatabase();\n        }\n    }\n}