using System;\nusing System.Security.Cryptography;\n\npublic class EmailTokenizer\n{\n    private readonly byte[] _key;\n\n    public EmailTokenizer(byte[] key)\n    {\n        _key = key;\n    }\n\n    public string Tokenize(string email)\n    {\n        using var hmac = new HMACSHA256(_key);\n        var hash = hmac.ComputeHash(Encoding.UTF8.GetBytes(email));\n        return Convert.ToBase64String(hash);\n    }\n\n    public string Detokenize(string token)\n    {\n        // Store the mapping between tokens and email addresses securely\n        // This example uses a simple dictionary, but you should use a secure storage mechanism\n        var mapping = new Dictionary<string, string>();\n        // ...\n\n        return mapping[token];\n    }\n}