// Web app\nusing Microsoft.AspNetCore.Mvc;\n\n[ApiController]\n[Route("api/[controller]")]\npublic class ProductsController : ControllerBase\n{\n    [HttpPost]\n    public IActionResult AddProduct([FromBody]Product product)\n    {\n        // Add product to web app DB\n        return Ok();\n    }\n\n    [HttpGet]\n    public IActionResult GetProducts()\n    {\n        // Return list of products from web app DB\n        return Ok();\n    }\n}\n\n// Local app\nusing System.Net.Http;\n\n// Send product data to web app\nvar product = new Product { Id = 1, Name = "Example Product" };\nvar client = new HttpClient();\nvar response = client.PostAsync("https://example.com/api/products", new StringContent(JsonConvert.SerializeObject(product), Encoding.UTF8, "application/json")).Result;\nif (response.IsSuccessStatusCode)\n{\n    // Product added successfully\n}