using System;\nusing System.IO;\nusing System.Text;\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        string filePath = @"path\to\your\file.txt";\n        int linesToRead = 10;\n\n        string[] lastLines = ReadLastLines(filePath, linesToRead);\n\n        foreach (string line in lastLines)\n        {\n            Console.WriteLine(line);\n        }\n    }\n\n    public static string[] ReadLastLines(string filePath, int linesToRead)\n    {\n        using (FileStream fileStream = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n        {\n            using (StreamReader reader = new StreamReader(fileStream, Encoding.UTF8, true))\n            {\n                string line;\n                Queue<string> lastLinesQueue = new Queue<string>(linesToRead);\n\n                while ((line = reader.ReadLine()) != null)\n                {\n                    if (lastLinesQueue.Count >= linesToRead)\n                    {\n                        lastLinesQueue.Dequeue();\n                    }\n\n                    lastLinesQueue.Enqueue(line);\n                }\n\n                string[] lastLines = new string[lastLinesQueue.Count];\n                lastLinesQueue.CopyTo(lastLines, 0);\n\n                return lastLines;\n            }\n        }\n    }\n}