using (SqlConnection sqlConnection = new SqlConnection(connectionString))\n{\n    sqlConnection.Open();\n    SqlTransaction transaction = sqlConnection.BeginTransaction();\n\n    try\n    {\n        // Get the latest date from SQL Server\n        SqlCommand maxDateCommand = new SqlCommand("SELECT MAX(RunDate) FROM ProductionRuns", sqlConnection, transaction);\n        DateTime maxDate = (DateTime)maxDateCommand.ExecuteScalar();\n\n        // Get new data from Access\n        OleDbCommand accessCommand = new OleDbCommand("SELECT * FROM ProductionRuns WHERE RunDate > ?", accessConnection);\n        accessCommand.Parameters.AddWithValue("@maxDate", maxDate);\n        OleDbDataReader accessReader = accessCommand.ExecuteReader();\n\n        // Migrate data to SQL Server\n        while (accessReader.Read())\n        {\n            // Transform data as needed\n            // ...\n\n            // Insert data into SQL Server\n            SqlCommand insertCommand = new SqlCommand("INSERT INTO ProductionRuns (RunDate, ...) VALUES (@RunDate, ...)", sqlConnection, transaction);\n            insertCommand.Parameters.AddWithValue("@RunDate", accessReader["RunDate"]);\n            // ...\n            insertCommand.ExecuteNonQuery();\n        }\n\n        transaction.Commit();\n    }\n    catch (Exception ex)\n    {\n        transaction.Rollback();\n        // Handle error\n    }\n}