using System;\nusing System.Windows.Forms;\nusing Microsoft.Office.InfoPath;\n\npublic class InfoPathEmbedder : Form\n{\n    private FormControl infoPathControl;\n\n    public InfoPathEmbedder()\n    {\n        infoPathControl = new FormControl();\n        this.Controls.Add(infoPathControl);\n        infoPathControl.Dock = DockStyle.Fill;\n        this.Text = "InfoPath Embedded";\n        this.Width = 800;\n        this.Height = 600;\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new InfoPathEmbedder());\n    }\n\n    private void OpenTemplate(string templatePath)\n    {\n        using (var templateStream = new System.IO.FileStream(templatePath, System.IO.FileMode.Open))\n        {\n            infoPathControl.Open(templateStream);\n        }\n    }\n}