using System.Security.Cryptography;\nusing System.IO;\n\nclass EncryptFolder\n{\n    public static void Main(string[] args)\n    {\n        string folderPath = @"C:\MyFolder";\n        string password = "mysecretpassword";\n\n        // Create a new AES encryption object\n        Aes aes = Aes.Create();\n        aes.Key = GetKeyFromPassword(password);\n\n        // Encrypt the folder contents\n        string[] files = Directory.GetFiles(folderPath);\n        foreach (string file in files)\n        {\n            byte[] fileBytes = File.ReadAllBytes(file);\n            byte[] encryptedBytes = aes.EncryptCbc(fileBytes);\n            File.WriteAllBytes(file, encryptedBytes);\n        }\n    }\n\n    static byte[] GetKeyFromPassword(string password)\n    {\n        // Create a password-based key derivation function\n        Rfc2898DeriveBytes deriveBytes = new Rfc2898DeriveBytes(password, 16);\n\n        // Derive a key from the password\n        byte[] key = deriveBytes.GetBytes(32);\n\n        return key;\n    }\n}