public partial class MyUserControl : UserControl\n{\n    public event EventHandler<ValidationErrorEventArgs> ValidationError;\n\n    private void ValidateInput()\n    {\n        // Perform validation...\n        if (!IsValid)\n        {\n            ValidationError?.Invoke(this, new ValidationErrorEventArgs("Error message"));\n        }\n    }\n}\n\npublic class ValidationErrorEventArgs : EventArgs\n{\n    public string ErrorMessage { get; set; }\n\n    public ValidationErrorEventArgs(string errorMessage)\n    {\n        ErrorMessage = errorMessage;\n    }\n}