protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        // Get the current row's value\n        decimal currentValue = Convert.ToDecimal(e.Row.Cells[1].Text);\n\n        // Get the running total from ViewState\n        decimal runningTotal = (decimal)ViewState["RunningTotal"];\n\n        // Add the current value to the running total\n        runningTotal += currentValue;\n\n        // Store the new running total in ViewState\n        ViewState["RunningTotal"] = runningTotal;\n\n        // Display the running total in the footer\n        GridView1.FooterRow.Cells[1].Text = "Running Total: " + runningTotal.ToString("c");\n    }\n}\n\nprotected void GridView1_DataBound(object sender, EventArgs e)\n{\n    // Initialize the running total to 0\n    ViewState["RunningTotal"] = 0;\n}