private void dataGridView1_SortCompare(object sender, DataGridViewSortCompareEventArgs e)\n{\n    if (e.Column.Index == yourCustomColumnIndex)\n    {\n        string cellValue1 = dataGridView1.Rows[e.RowIndex1].Cells[e.Column.Index].FormattedValue.ToString();\n        string cellValue2 = dataGridView1.Rows[e.RowIndex2].Cells[e.Column.Index].FormattedValue.ToString();\n\n        e.SortResult = string.Compare(cellValue1, cellValue2);\n        e.Handled = true;\n    }\n}