public class CompositeKeyComparer : IEqualityComparer<Person>\n{\n    public bool Equals(Person x, Person y)\n    {\n        return x.compositeKey == y.compositeKey;\n    }\n\n    public int GetHashCode(Person obj)\n    {\n        return obj.compositeKey.GetHashCode();\n    }\n}\n\n// ...\n\nList<Person> people = GetFromDB;\nList<Exclusions> exclusions = GetFromOtherDB;\n\nvar filteredResults = people.Except(exclusions.Select(e => new Person { compositeKey = e.compositeKey }), new CompositeKeyComparer()).ToList();