using System.CommandLine;\nusing System.CommandLine.Invocation;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        var rootCommand = new RootCommand("My command line tool");\n\n        rootCommand.AddCommand(new Command("greet", "Say hello")\n        {\n            new Option<string>("--name", "Your name")\n        });\n\n        rootCommand.Handler = CommandHandler.Create<IConsole, string>(async (console, name) =>\n        {\n            await console.Out.WriteLineAsync($"Hello {name}!");\n        });\n\n        await rootCommand.InvokeAsync(args);\n    }\n}