// ASPX page\n<asp:ScriptManager ID="ScriptManager1" runat="server" />\n<asp:UpdatePanel ID="UpdatePanel1" runat="server" UpdateMode="Conditional">\n    <ContentTemplate>\n        <!-- Your content here -->\n    </ContentTemplate>\n</asp:UpdatePanel>\n<asp:HiddenField ID="TriggerField" runat="server" Value="false" />\n\n// Code-behind\nprotected void Page_PreRender(object sender, EventArgs e)\n{\n    ScriptManager.RegisterStartupScript(this, this.GetType(), "delayedLoad", @"\n        setTimeout(function() {\n            document.getElementById(""TriggerField"").value = ""true"";\n        }, 1000);\n    ", true);\n}\n\nprotected void UpdatePanel1_PreRender(object sender, EventArgs e)\n{\n    if (TriggerField.Value == "true")\n    {\n        // Update content here\n        UpdatePanel1.Update();\n    }\n}