protected void GridViewA_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        decimal price = (decimal)e.Row.Cells[/* column index of price column */].Text;\n        ViewState["TotalPrice"] = (decimal)ViewState["TotalPrice"] + price;\n    }\n}\n\nprotected void GridViewA_DataBound(object sender, EventArgs e)\n{\n    TextBoxTotalPrice.Text = ViewState["TotalPrice"].ToString("F2");\n}