public partial class Login : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string authHeader = Request.Headers["Authorization"];\n        if (!string.IsNullOrEmpty(authHeader))\n        {\n            NovellAuthenticator authenticator = new NovellAuthenticator("your-novell-server", "your-novell-port", "your-search-base");\n            string username = authenticator.GetUsername(authHeader);\n            if (username != null)\n            {\n                // User is authenticated, proceed with your logic\n            }\n            else\n            {\n                Response.StatusCode = 401;\n                Response.StatusDescription = "Invalid credentials";\n            }\n        }\n        else\n        {\n            Response.StatusCode = 401;\n            Response.StatusDescription = "Authentication required";\n        }\n    }\n}