using System.Diagnostics;\n\n// Create a new Process object\nProcess javaProcess = new Process();\n\n// Set the FileName to the path of the java.exe executable\njavaProcess.FileName = @"C:\Path\To\java.exe";\n\n// Set the Arguments to the command-line arguments\njavaProcess.Arguments = @"-Xmx1024m -cp ./../libs/*;l2jserver.jar net.sf.l2j.gameserver.GameServer";\n\n// Redirect the output to capture it\njavaProcess.RedirectStandardOutput = true;\njavaProcess.RedirectStandardError = true;\n\n// Start the process\njavaProcess.Start();\n\n// Read the output\nstring output = javaProcess.StandardOutput.ReadToEnd();\nstring error = javaProcess.StandardError.ReadToEnd();\n\n// Wait for the process to exit\njavaProcess.WaitForExit();\n\n// Print the output to a textbox or handle it as needed\ntextbox.Text = output;