public static void MergeColumns(string format, string mergedColumn, DataTable dt, params string[] columnsToMerge)\n{\n    // Check if the merged column already exists\n    if (dt.Columns.Contains(mergedColumn))\n    {\n        // If it exists, remove it\n        dt.Columns.Remove(mergedColumn);\n    }\n\n    // Add the new merged column\n    dt.Columns.Add(mergedColumn, typeof(string));\n\n    // Loop through each row in the DataTable\n    foreach (DataRow row in dt.Rows)\n    {\n        // Create an array to hold the values to be merged\n        string[] values = new string[columnsToMerge.Length];\n\n        // Populate the array with the values from the columns to be merged\n        for (int i = 0; i < columnsToMerge.Length; i++)\n        {\n            values[i] = row[columnsToMerge[i]].ToString();\n        }\n\n        // Use the format to merge the values and assign it to the new column\n        row[mergedColumn] = string.Format(format, values);\n    }\n}