using MailKit.Net.Smtp;\nusing MailKit.Security;\nusing MimeKit;\n\nvar message = new MimeMessage();\nmessage.To.Add(new MailboxAddress("recipient@example.com"));\nmessage.Subject = "Documentation";\nmessage.Body = new TextPart("plain") { Text = "Infinitely cool body here" };\n\nvar client = new SmtpClient();\nclient.Connect("mysmtphost", 465, SecureSocketOptions.SslOnConnect);\nclient.Authenticate("usernamehere", "passwordhere");\nclient.Send(message);\nclient.Disconnect(true);