using System;\nusing System.Collections.Generic;\n\npublic class RelationshipGraph\n{\n    private Dictionary<int, List<Edge>> adjacencyList;\n\n    public RelationshipGraph()\n    {\n        adjacencyList = new Dictionary<int, List<Edge>>();\n    }\n\n    public void AddValue(int value)\n    {\n        adjacencyList[value] = new List<Edge>();\n    }\n\n    public void AddRelationship(int from, int to, int priority)\n    {\n        adjacencyList[from].Add(new Edge(to, priority));\n        adjacencyList[to].Add(new Edge(from, priority)); // assuming relationships are bidirectional\n    }\n\n    public List<Edge> FindShortestPath(int[] values)\n    {\n        // implement Dijkstra's algorithm or A* algorithm to find the shortest path\n        // taking into account relationship priorities\n    }\n}\n\npublic class Edge\n{\n    public int To { get; set; }\n    public int Priority { get; set; }\n\n    public Edge(int to, int priority)\n    {\n        To = to;\n        Priority = priority;\n    }\n}