public class CustomServiceAuthorizationManager : ServiceAuthorizationManager\n{\n    protected override bool CheckAccess(OperationContext operationContext, ref Message message)\n    {\n        // Get the host name of the request\n        var hostName = operationContext.RequestContext.RequestMessage.Headers.Host;\n\n        // Check if the host name matches your application's host name\n        if (hostName == "your-application-host-name")\n        {\n            // Allow access to all operations\n            return true;\n        }\n        else\n        {\n            // Only allow access to the Get operation\n            var operationName = operationContext.RequestContext.RequestMessage.Headers.Action;\n            if (operationName == "Get")\n            {\n                return true;\n            }\n            else\n            {\n                return false;\n            }\n        }\n    }\n}