using System.Runtime.InteropServices;\nusing WMPLib;\n\nclass MediaPlayerController\n{\n    [DllImport("wmp.dll")]\n    private static extern IntPtr WMCreatePlayer(IntPtr hwndParent);\n\n    private IWMPPlayer _player;\n    private IWMPControls _controls;\n\n    public MediaPlayerController()\n    {\n        _player = (IWMPPlayer)Marshal.GetObjectForIUnknown(WMCreatePlayer(IntPtr.Zero));\n        _controls = (IWMPControls)_player.Controls;\n    }\n\n    public void SetPlaybackSpeed(double speed)\n    {\n        _controls.PlaybackSpeed = speed;\n    }\n\n    public double GetPlaybackSpeed()\n    {\n        return _controls.PlaybackSpeed;\n    }\n}