using System;\nusing System.Web;\n\npublic class Rfc3986Escaper\n{\n    public static string Escape(string value)\n    {\n        // Uri.EscapeDataString is not compatible with RFC 3986, so we use HttpUtility.UrlEncode\n        // with some additional replacements to make it compliant.\n        var escaped = HttpUtility.UrlEncode(value).Replace("+", "%20");\n        return escaped;\n    }\n}