DataTable table1 = new DataTable();\ntable1.Columns.Add("Column1", typeof(string));\ntable1.Columns.Add("Column2", typeof(string));\n\nDataTable table2 = new DataTable();\ntable2.Columns.Add("Column1", typeof(string));\ntable2.Columns.Add("Column2", typeof(string));\n\n// Add some data to both tables\ntable1.Rows.Add("1", "one");\ntable1.Rows.Add("2", "two");\n\ntable2.Rows.Add("3", "three");\ntable2.Rows.Add("4", "four");\n\n// Merge table2 into table1\ntable1.Merge(table2);\n\n// Print the contents of table1\nforeach (DataRow row in table1.Rows)\n{\n    Console.WriteLine(row["Column1"] + " " + row["Column2"]);\n}