using NHibernate;\nusing NHibernate.Xml;\n\n// Assume you have an entity class 'User'\npublic class User\n{\n    public virtual int Id { get; set; }\n    public virtual string Name { get; set; }\n}\n\n// Create an NHibernate session factory\nISessionFactory sessionFactory = ...;\n\n// Get an instance of the entity\nUser user = sessionFactory.OpenSession().Get<User>(1);\n\n// Serialize the entity to XML\nXmlSerializer serializer = new XmlSerializer(typeof(User));\nstring xml = serializer.Serialize(user);