using System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define a unique name for the mutex\n        string mutexName = "MyServiceAppMutex";\n\n        // Try to create a new mutex with the given name\n        Mutex mutex = new Mutex(false, mutexName);\n\n        // If the mutex already exists, it means the service app is running\n        if (!mutex.WaitOne(0, false))\n        {\n            Console.WriteLine("Service app is already running.");\n            // You can shut down your app here\n            return;\n        }\n\n        // If the mutex doesn't exist, it means the service app is not running\n        Console.WriteLine("Service app is not running.");\n\n        // Don't forget to release the mutex when you're done\n        mutex.ReleaseMutex();\n    }\n}