using System.Diagnostics;\n\nstring filePath = @"C:\path\to\file.txt";\nProcess[] processes = Process.GetProcesses();\nforeach (Process p in processes)\n{\n    if (p.Modules.Cast<ProcessModule>().Any(m => m.FileName == filePath))\n    {\n        Console.WriteLine("File is open from another process");\n        return;\n    }\n}\nConsole.WriteLine("File is not open from another process");