[WebMethod]\npublic static string SaveData(string name, string email)\n{\n    // Connect to SQL Server 2005 database\n    string connectionString = ConfigurationManager.ConnectionStrings["YourDBConnectionString"].ConnectionString;\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n        SqlCommand command = new SqlCommand("INSERT INTO YourTable (Name, Email) VALUES (@Name, @Email)", connection);\n        command.Parameters.AddWithValue("@Name", name);\n        command.Parameters.AddWithValue("@Email", email);\n        command.ExecuteNonQuery();\n    }\n    return "Data saved successfully!";\n}