using System.Threading;\n\nclass ServiceApp\n{\n    static Mutex mutex;\n\n    static void Main()\n    {\n        // Define a unique name for the mutex\n        string mutexName = "MyServiceAppMutex";\n\n        // Create a new mutex with the given name\n        mutex = new Mutex(false, mutexName);\n\n        // Start your service app here...\n\n        // When your service app exits, release the mutex\n        mutex.ReleaseMutex();\n    }\n}