using System.Net.Http;\nusing System.Text;\n\n// Create a new HttpClient instance\nvar client = new HttpClient();\n\n// Set the request method to PUT\nvar request = new HttpRequestMessage(HttpMethod.Put, "http://example.com/resource");\n\n// Add the data to be sent in the request body\nvar data = "Hello, World!";\nrequest.Content = new StringContent(data, Encoding.UTF8, "text/plain");\n\n// Send the request\nvar response = await client.SendAsync(request);\n\n// Check the response status code\nif (response.IsSuccessStatusCode)\n{\n    Console.WriteLine("PUT request successful!");\n}\nelse\n{\n    Console.WriteLine("PUT request failed: " + response.StatusCode);\n}