public class WebService\n{\n    private Cache _cache;\n\n    public WebService()\n    {\n        _cache = new Cache();\n    }\n\n    public void Start()\n    {\n        ComComponent comComponent = new ComComponent();\n        _cache.Insert("ComComponent", comComponent);\n    }\n\n    public void Shutdown()\n    {\n        _cache.Remove("ComComponent");\n    }\n\n    public string GetCurrentState()\n    {\n        ComComponent comComponent = (ComComponent)_cache["ComComponent"];\n        return comComponent.GetCurrentState();\n    }\n}