using System;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Windows.Media;\nusing System.Windows.Media.Animation;\n\n// Master computer code\npublic class MasterComputer\n{\n    private TcpListener _listener;\n    private MediaTimeline _mediaTimeline;\n\n    public MasterComputer()\n    {\n        _listener = new TcpListener(IPAddress.Any, 8080);\n        _listener.Start();\n\n        _mediaTimeline = new MediaTimeline(new Uri("video1.mp4"));\n        _mediaTimeline.BeginTime = TimeSpan.Zero;\n    }\n\n    public void Start()\n    {\n        // Send synchronization signal to slave computers\n        byte[] signal = Encoding.ASCII.GetBytes("Start");\n        _listener.GetStream().Write(signal, 0, signal.Length);\n\n        // Start video playback\n        _mediaTimeline.Begin();\n    }\n}\n\n// Slave computer code\npublic class SlaveComputer\n{\n    private TcpClient _client;\n    private MediaTimeline _mediaTimeline;\n\n    public SlaveComputer()\n    {\n        _client = new TcpClient();\n        _client.Connect("master_computer_ip", 8080);\n\n        _mediaTimeline = new MediaTimeline(new Uri("video2.mp4"));\n        _mediaTimeline.BeginTime = TimeSpan.Zero;\n    }\n\n    public void Start()\n    {\n        // Receive synchronization signal from master computer\n        byte[] signal = new byte[1024];\n        _client.GetStream().Read(signal, 0, signal.Length);\n\n        // Start video playback\n        _mediaTimeline.Begin();\n    }\n}