using CommandLine;\n\nclass MyOptions\n{\n    [Option('t', "target", Required = true, HelpText = "Specify the target")]\n    public string Target { get; set; }\n\n    [Option('c', "config", HelpText = "Specify the config file")]\n    public string ConfigFile { get; set; }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var parser = new CommandLineParser();\n        var options = new MyOptions();\n\n        if (parser.ParseArguments(args, options))\n        {\n            // Use the parsed options\n            Console.WriteLine($"Target: {options.Target}");\n            Console.WriteLine($"Config file: {options.ConfigFile}");\n        }\n        else\n        {\n            Console.WriteLine("Invalid arguments. Use --help for usage.");\n        }\n    }\n}